INSERT INTO ALBUMS (ID_ALBUM, MOISPARUTION, ANNEEPARUTION, ID_SERIE, TOME, TOMEDEBUT, TOMEFIN, HORSSERIE, INTEGRALE, ACHAT, NBEDITIONS, INITIALETITREALBUM, DC_ALBUMS, DM_ALBUMS, TITREALBUM, SOUNDEXTITREALBUM, NOTATION, SUJETALBUM, REMARQUESALBUM) VALUES ('{51FE6CD2-320B-4359-9327-85B4BCDC18F8}', 3, 2009, '{153F112D-A71B-4CC2-B36F-3363466E1C19}', 31, NULL, NULL, 0, 0, 0, 1, 'D', '04/03/2009 20:04:01.512', '07/03/2011 19:36:24.418', 'Don''t kill my volupture', '35324594136', NULL, 'Ichigo se retrouve face à Ulquiora, lEspada numéro 4 venu pour lui apprendre la mort de Rukia, mais surtout le défi er en combat singulier ! Afi n décourter le duel, Ichigo met toutes ses forces dans la bataille, mais même ses pouvoirs de Hollow ne peuvent rien face à lennemi, qui lui transperce le coeur ! Pendant ce temps, Orihime subit les tortures de ses geôlières, avant dêtre secourue par Grimmjow. Ce dernier décide de se servir delle pour guérir Ichigo afi n de régler ses comptes avec lui
