INSERT INTO ALBUMS (ID_ALBUM, MOISPARUTION, ANNEEPARUTION, ID_SERIE, TOME, TOMEDEBUT, TOMEFIN, HORSSERIE, INTEGRALE, ACHAT, NBEDITIONS, INITIALETITREALBUM, DC_ALBUMS, DM_ALBUMS, TITREALBUM, SOUNDEXTITREALBUM, NOTATION, SUJETALBUM, REMARQUESALBUM) VALUES ('{8D468FB1-0465-4F9B-9147-7DD586F13361}', 2, 2006, '{1FCAC0F3-1B40-4668-A152-56CDC18C093C}', 19, NULL, NULL, 0, 0, 0, 1, NULL, '03/10/2006 01:20:11.000', '07/03/2011 19:36:24.491', NULL, NULL, NULL, 'L''homme à la guitare file vers Tokyo. Il arrive au mur d''enceinte de la ville. Tant de gens sont arrêtés et assassinés pour avoir tenté de la franchir sans laissez-passer. Tant de corps sont transportés et enterrés. Qui pourra changer tout ça ? Qui pourra passer la barrière ? Qui attend au bout du chemin ? Il est revenu...', NULL);
INSERT INTO ALBUMS (ID_ALBUM, MOISPARUTION, ANNEEPARUTION, ID_SERIE, TOME, TOMEDEBUT, TOMEFIN, HORSSERIE, INTEGRALE, ACHAT, NBEDITIONS, INITIALETITREALBUM, DC_ALBUMS, DM_ALBUMS, TITREALBUM, SOUNDEXTITREALBUM, NOTATION, SUJETALBUM, REMARQUESALBUM) VALUES ('{8D77FA6F-9B57-4900-BBB5-17513C9CB479}', 4, 2011, '{47B25DEA-3A37-4F2C-AB5A-5BB90A4C7ED7}', 7, NULL, NULL, 0, 0, 0, 1, 'E', '04/15/2011 17:55:27.850', '07/03/2011 19:36:24.763', 'Expédition Vanikoro', '81359526', NULL, 'D''un côté, Gavroche et Zelda, à bord du sous-marin nordiste le Kearsarge ; de l''autre, le Diable de Tasmanie, rallié par le Fantôme de Paris, à bord de l''Alabama, navire de guerre sudiste. Les deux équipages se disputent les Îles Salomon afin de récupérer la croix de la Pérouse. L''expédition les mènera au-delà de la faille de Vanikoro, là où sommeille une cité sous-marine inconnue de l''espèce humaine...
INSERT INTO ALBUMS (ID_ALBUM, MOISPARUTION, ANNEEPARUTION, ID_SERIE, TOME, TOMEDEBUT, TOMEFIN, HORSSERIE, INTEGRALE, ACHAT, NBEDITIONS, INITIALETITREALBUM, DC_ALBUMS, DM_ALBUMS, TITREALBUM, SOUNDEXTITREALBUM, NOTATION, SUJETALBUM, REMARQUESALBUM) VALUES ('{8D9CD236-F0E3-487B-9FFB-4A464B41D94E}', 6, 2005, '{51843D8B-0CF3-4BFC-8531-CA606DCE9D16}', 8, NULL, NULL, 0, 0, 0, 1, NULL, '05/19/2005 20:14:26.000', '07/03/2011 19:36:24.414', NULL, NULL, NULL, 'A l''approche du jour anniversaire de la mort d''Hideyuki Makimura, le frère aîné de Kaori, Xiang-Ying remarque avec inquiétude que le comportement de Saeko est de plus en plus étrange... Alors que la mémoire de Kaori dévoile à Xiang-Ying le triste passé de Saeko et Makimura, le rideau se lève sur une nouvelle tragédie : le tueur en série qui a assassiné Makimura a décidé de s''en prendre à Saeko !', NULL);
