INSERT INTO ALBUMS (ID_ALBUM, MOISPARUTION, ANNEEPARUTION, ID_SERIE, TOME, TOMEDEBUT, TOMEFIN, HORSSERIE, INTEGRALE, ACHAT, NBEDITIONS, INITIALETITREALBUM, DC_ALBUMS, DM_ALBUMS, TITREALBUM, SOUNDEXTITREALBUM, NOTATION, SUJETALBUM, REMARQUESALBUM) VALUES ('{B510A77A-6BB4-420E-AC93-1E45899D132C}', 9, 2006, '{B9DCA7B4-13C8-405C-B6F7-936304006AD0}', 3, NULL, NULL, 0, 0, 0, 1, 'T', '09/24/2006 09:35:36.000', '07/03/2011 19:36:24.555', 'Tidia', '3', NULL, 'Embarqués dans de nouveaux mondes parallèles, Pixie et ses compagnons de fortune n''arrivent plus à contrôler les rêves dévastateurs du jeune prince Aël. Après avoir tout fait pour l''empêcher de s''endormir, ils demandent l''aide du peuple Pixie pour tenter de l''hypnotiser... En vain ! C''est sans compter sur les projets machiavéliques du seigneur Ankou.
