INSERT INTO ALBUMS (ID_ALBUM, MOISPARUTION, ANNEEPARUTION, ID_SERIE, TOME, TOMEDEBUT, TOMEFIN, HORSSERIE, INTEGRALE, ACHAT, NBEDITIONS, INITIALETITREALBUM, DC_ALBUMS, DM_ALBUMS, TITREALBUM, SOUNDEXTITREALBUM, NOTATION, SUJETALBUM, REMARQUESALBUM) VALUES ('{BDF714D3-A18E-4F1D-8F1C-60801F2F004B}', 10, 2006, '{9AE3D565-E8E0-49C8-9937-092AA338387B}', 3, NULL, NULL, 0, 0, 0, 1, 'P', '10/20/2006 10:34:54.000', '07/03/2011 19:36:24.555', 'printemps [Le]', '16535184', NULL, 'Arrivé au terme de sa vie, Mowgli continue de se souvenir. Et ses mots font revivre une époque où le petit de l''homme était roi de la jungle, le tueur de Shere Kahn, le frère des loups et des panthères ! Il lui restait à découvrir que la beauté et la grâce féminine sont un feu bien plus ardent et dévastateur que celui qui lui permit de tuer le tigre. Et qu''une grande ville déchirée par la colonisation britannique pouvait se révéler une jungle bien plus dangereuse que tout ce qu''il avait connu auparavant...
