INSERT INTO SERIES (ID_SERIE, ID_EDITEUR, ID_COLLECTION, TERMINEE, COMPLETE, SUIVREMANQUANTS, SUIVRESORTIES, INITIALETITRESERIE, DC_SERIES, DM_SERIES, NB_ALBUMS, VO, COULEUR, TITRESERIE, SOUNDEXTITRESERIE, NOTATION, SITEWEB, SUJETSERIE, REMARQUESSERIE, ETAT, RELIURE, TYPEEDITION, ORIENTATION, FORMATEDITION, SENSLECTURE) VALUES ('{0FD1223D-A843-4818-AA48-A3D1B8E3ECC8}', '{0833D47E-BE4F-4522-800C-ECBEF7526AA5}', NULL, 1, 1, 0, 0, 'A', '04/03/2005 17:23:28.000', '07/25/2012 20:19:27.301', NULL, NULL, NULL, 'aquanautes [Les]', '253848', NULL, NULL, 'Océan Atlantique nord : l''armée des Etats-Unis a investi la station scientifique « PHYSALIA », située à plus de cinq cents mètres de profondeur. Elle doit mener à bien une mission dont les géologues ignorent tout. Survient une série de meurtres qui met en émoi la population de la station. De retour d''une mission sous-marine, le géologue Nando Mc Rae est témoin de l''assassinat d''un militaire. Selon toute logique, le meurtre n''a pas pu avoir lieu. Mais le géologue reste convaincu que ce qu''il a vu est bien réel.
INSERT INTO SERIES (ID_SERIE, ID_EDITEUR, ID_COLLECTION, TERMINEE, COMPLETE, SUIVREMANQUANTS, SUIVRESORTIES, INITIALETITRESERIE, DC_SERIES, DM_SERIES, NB_ALBUMS, VO, COULEUR, TITRESERIE, SOUNDEXTITRESERIE, NOTATION, SITEWEB, SUJETSERIE, REMARQUESSERIE, ETAT, RELIURE, TYPEEDITION, ORIENTATION, FORMATEDITION, SENSLECTURE) VALUES ('{11AC007A-2B3E-4A37-972A-527EA2463316}', '{F8EF1240-1892-4BA0-88B6-09D5B701120C}', NULL, 0, 0, 1, 1, 'T', '04/03/2005 17:23:28.000', '07/25/2012 20:19:27.301', NULL, NULL, NULL, 'Tuniques Bleues [Les]', '352814848', NULL, NULL, 'Le gros, c''est Cornélius Chesterfield, sergent zélé et discipliné de l''armée du Nord des Etats-Unis. Le petit, c''est Blutch, un malin râleur et désabusé qui ne rêve que de déserter. Pris dans les affres de la Guerre de Sécession, ces deux-là font ce qu''ils peuvent pour échapper aux ennuis que leur valent des chefs bornés, des ordres aberrants et un destin décidément contraire...', 'À travers des histoires pleines de rire et d''action, Lambil et Cauvin nous offrent une critique acerbe des absurdités de la guerre et du militarisme obtus. Un nouveau classique de la BD familiale.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO SERIES (ID_SERIE, ID_EDITEUR, ID_COLLECTION, TERMINEE, COMPLETE, SUIVREMANQUANTS, SUIVRESORTIES, INITIALETITRESERIE, DC_SERIES, DM_SERIES, NB_ALBUMS, VO, COULEUR, TITRESERIE, SOUNDEXTITRESERIE, NOTATION, SITEWEB, SUJETSERIE, REMARQUESSERIE, ETAT, RELIURE, TYPEEDITION, ORIENTATION, FORMATEDITION, SENSLECTURE) VALUES ('{134CD32F-DDF2-4E3B-80E5-8A39C3FE6891}', '{0833D47E-BE4F-4522-800C-ECBEF7526AA5}', NULL, 0, 0, 1, 1, 'S', '04/03/2005 17:23:28.000', '07/25/2012 20:19:27.298', NULL, NULL, NULL, 'Sky Doll', '8234', NULL, NULL, 'Dieu est mort. Son corps gît dans son bureau de l''Heaven Spaceship Wash, un astrolavage parmi d''autres sur la planète Papathéa. Mais quel est le lien entre le créateur de l''univers et cette poupée synthétique concue pour le plaisir des sens, en cavale de planète en planète? Pourquoi semble-t-elle toujours en proie à une avalanche d''évènements désastreux, tels des électrons gravitant autour du noyau d''un atome? Peut-être que la papesse sait ou pressent quelque chose. Peut-être ce secret est-il gardé par les mystérieux habitants de la ville blanche. Peut-être, tout simplement, que personne ne sait rien. Sans doute, lorsque la vérité éclatera au grand jour, sera-t-il déjà trop tard. Pour tous. ', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
